import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import ExperienceCard from './ExperienceCard';

const PublicPortfolio = () => {
  const { username } = useParams();
  const [data, setData] = useState({ user: { profile: { links: {}, skills: [] } }, experiences: [] });
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get(`/api/portfolio/${username}`);
        setData(res.data);
      } catch (err) {
        setError('Error loading portfolio: ' + (err.response?.data?.error || 'Unknown error'));
      }
    };
    fetchData();
  }, [username]);

  if (error) return <div className="p-4 text-red-500">{error}</div>;

  return (
    <div className="p-4">
      <h1 className="text-3xl mb-4">{data.user.name}'s Portfolio</h1>
      <p className="mb-4">{data.user.profile.bio}</p>
      <h3 className="text-lg mb-2">Skills:</h3>
      <ul className="list-disc mb-4 pl-6">
        {data.user.profile.skills.map((skill, idx) => <li key={idx}>{skill}</li>)}
      </ul>
      <h3 className="text-lg mb-2">Links:</h3>
      <div className="mb-4">
        {Object.entries(data.user.profile.links).map(([key, url]) => url && (
          <a key={key} href={url} target="_blank" rel="noopener noreferrer" className="mr-4 text-blue-500 hover:underline">{key}</a>
        ))}
      </div>
      <h2 className="text-2xl mb-2">Experiences</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {data.experiences.map(exp => <ExperienceCard key={exp._id} exp={exp} role="public" />)}
      </div>
    </div>
  );
};

export default PublicPortfolio;
