import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Register = ({ setToken }) => {
  const [formData, setFormData] = useState({
    role: 'student',
    name: '',
    email: '',
    password: '',
  });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('/api/auth/register', formData);
      localStorage.setItem('token', res.data.token);
      setToken(res.data.token);
      const role = JSON.parse(atob(res.data.token.split('.')[1])).role;
      navigate(role === 'student' ? '/student-dashboard' : '/org-dashboard');
    } catch (err) {
      alert('Registration failed: ' + (err.response?.data?.error || 'Unknown error'));
    }
  };

  return (
    <form onSubmit={handleSubmit} className="max-w-md mx-auto p-4 bg-white shadow rounded">
      <h2 className="text-2xl mb-4 text-center">Register</h2>
      <select name="role" value={formData.role} onChange={handleChange} className="border p-2 w-full mb-2 rounded" required>
        <option value="student">Student</option>
        <option value="organization">Organization</option>
      </select>
      <input type="text" name="name" value={formData.name} onChange={handleChange} placeholder="Name" className="border p-2 w-full mb-2 rounded" required />
      <input type="email" name="email" value={formData.email} onChange={handleChange} placeholder="Email" className="border p-2 w-full mb-2 rounded" required />
      <input type="password" name="password" value={formData.password} onChange={handleChange} placeholder="Password" className="border p-2 w-full mb-2 rounded" required />
      <button type="submit" className="bg-blue-500 text-white p-2 w-full rounded hover:bg-blue-600">Register</button>
    </form>
  );
};

export default Register;
