import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import api from '../services/api';

function StudentDashboard() {
  const [experiences, setExperiences] = useState([]);
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchExperiences = async () => {
      try {
        const response = await api.get(`/api/experience/student/${user.email}`);
        setExperiences(response.data);
      } catch (err) {
        alert('Failed to fetch experiences: ' + err.message);
      }
    };
    fetchExperiences();
  }, [user.email]);

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Student Dashboard - {user.name}</h2>
      <button onClick={handleLogout} className="mb-4 bg-red-500 text-white p-2 rounded hover:bg-red-600">
        Logout
      </button>
      <h3 className="text-xl mb-2">Experiences</h3>
      {experiences.length === 0 ? (
        <p>No experiences yet.</p>
      ) : (
        <ul className="list-disc pl-5">
          {experiences.map((exp) => (
            <li key={exp._id} className="mb-2">
              {exp.activityName} - {exp.status}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default StudentDashboard;
