import React, { useEffect, useState } from 'react';
import axios from 'axios';
import ExperienceCard from './ExperienceCard';
import PortfolioBuilder from './PortfolioBuilder';

const StudentDashboard = ({ token }) => {
  const [experiences, setExperiences] = useState([]);
  const [user, setUser] = useState({ profile: { bio: '', skills: [], links: { github: '', leetcode: '', figma: '', linkedin: '' } } });

  useEffect(() => {
    const fetchData = async () => {
      const config = { headers: { Authorization: `Bearer ${token}` } };
      try {
        const userRes = await axios.get('/api/user', config);
        setUser(userRes.data || { profile: { bio: '', skills: [], links: { github: '', leetcode: '', figma: '', linkedin: '' } } });
        const expRes = await axios.get('/api/experience', config);
        setExperiences(expRes.data);
      } catch (err) {
        alert('Error fetching data: ' + (err.response?.data?.error || 'Unknown error'));
      }
    };
    fetchData();
  }, [token]);

  const updateExperiences = async () => {
    const config = { headers: { Authorization: `Bearer ${token}` } };
    const expRes = await axios.get('/api/experience', config);
    setExperiences(expRes.data);
  };

  const handleAccept = async (id) => {
    const config = { headers: { Authorization: `Bearer ${token}` } };
    try {
      await axios.post(`/api/experience/${id}/accept`, {}, config);
      updateExperiences();
    } catch (err) {
      alert('Error: ' + (err.response?.data?.error || 'Unknown error'));
    }
  };

  const handleDecline = async (id) => {
    const config = { headers: { Authorization: `Bearer ${token}` } };
    try {
      await axios.post(`/api/experience/${id}/decline`, {}, config);
      updateExperiences();
    } catch (err) {
      alert('Error: ' + (err.response?.data?.error || 'Unknown error'));
    }
  };

  const toggleFeatured = async (id) => {
    const config = { headers: { Authorization: `Bearer ${token}` } };
    try {
      await axios.patch(`/api/experience/${id}/feature`, {}, config);
      updateExperiences();
    } catch (err) {
      alert('Error: ' + (err.response?.data?.error || 'Unknown error'));
    }
  };

  return (
    <div className="p-4">
      <h1 className="text-2xl mb-4">Student Dashboard</h1>
      <PortfolioBuilder user={user} token={token} setUser={setUser} />
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {experiences.map(exp => (
          <ExperienceCard key={exp._id} exp={exp} onAccept={handleAccept} onDecline={handleDecline} onToggleFeatured={toggleFeatured} role="student" />
        ))}
      </div>
    </div>
  );
};

export default StudentDashboard;
