import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaSignOutAlt } from 'react-icons/fa';
import ExperienceCard from './ExperienceCard';

const OrgDashboard = ({ token, logout }) => {
  const [formData, setFormData] = useState({ activityName: '', achievementLevel: '', description: '', date: '', studentEmail: '' });
  const [files, setFiles] = useState([]);
  const [experiences, setExperiences] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchExperiences = async () => {
      setLoading(true);
      const config = { headers: { Authorization: `Bearer ${token}` } };
      try {
        const res = await axios.get('/api/experience', config);
        setExperiences(res.data);
      } catch (err) {
        alert('Error fetching: ' + (err.response?.data?.error || 'Unknown error'));
      } finally {
        setLoading(false);
      }
    };
    fetchExperiences();
  }, [token]);

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleFileChange = (e) => setFiles(e.target.files);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    Object.keys(formData).forEach(key => data.append(key, formData[key]));
    Array.from(files).forEach(file => data.append('documents', file));
    const config = { headers: { Authorization: `Bearer ${token}`, 'Content-Type': 'multipart/form-data' } };
    try {
      if (editingId) {
        await axios.patch(`/api/experience/${editingId}`, data, config);
        setEditingId(null);
      } else {
        await axios.post('/api/experience', data, config);
      }
      const res = await axios.get('/api/experience', { headers: { Authorization: `Bearer ${token}` } });
      setExperiences(res.data);
      setFormData({ activityName: '', achievementLevel: '', description: '', date: '', studentEmail: '' });
      setFiles([]);
    } catch (err) {
      alert('Error: ' + (err.response?.data?.error || 'Unknown error'));
    }
  };

  const handleEdit = (exp) => {
    setFormData({
      activityName: exp.activityName,
      achievementLevel: exp.achievementLevel,
      description: exp.description,
      date: exp.date.split('T')[0],
      studentEmail: exp.studentEmail,
    });
    setEditingId(exp._id);
  };

  const handleDelete = async (id) => {
    const config = { headers: { Authorization: `Bearer ${token}` } };
    try {
      await axios.delete(`/api/experience/${id}`, config);
      setExperiences(prev => prev.filter(exp => exp._id !== id));
    } catch (err) {
      alert('Error: ' + (err.response?.data?.error || 'Unknown error'));
    }
  };

  if (loading) return <div className="flex justify-center items-center h-screen"><div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500"></div></div>;

  return (
    <div className="p-6 max-w-7xl mx-auto">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold text-gray-900">Organization Dashboard</h1>
        <button onClick={logout} className="flex items-center text-red-600 hover:text-red-800">
          <FaSignOutAlt className="mr-2" /> Logout
        </button>
      </div>
      <div className="bg-white p-6 shadow-lg rounded-lg mb-8">
        <h2 className="text-2xl font-semibold text-gray-800 mb-4">Add/Edit Experience</h2>
        <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <input name="activityName" value={formData.activityName} onChange={handleChange} placeholder="Activity Name" className="p-2 border rounded focus:ring-2 focus:ring-blue-500" required />
          <input name="achievementLevel" value={formData.achievementLevel} onChange={handleChange} placeholder="Achievement Level" className="p-2 border rounded focus:ring-2 focus:ring-blue-500" required />
          <textarea name="description" value={formData.description} onChange={handleChange} placeholder="Description" className="p-2 border rounded md:col-span-2 focus:ring-2 focus:ring-blue-500" required />
          <input type="date" name="date" value={formData.date} onChange={handleChange} className="p-2 border rounded focus:ring-2 focus:ring-blue-500" required />
          <input name="studentEmail" value={formData.studentEmail} onChange={handleChange} placeholder="Student Email" className="p-2 border rounded focus:ring-2 focus:ring-blue-500" required />
          <input type="file" multiple onChange={handleFileChange} className="md:col-span-2" />
          <button type="submit" className="bg-green-500 text-white p-2 rounded hover:bg-green-600 md:col-span-2">{editingId ? 'Update' : 'Add'} Experience</button>
        </form>
      </div>
      <h2 className="text-2xl font-semibold text-gray-800 mb-4">Managed Experiences</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {experiences.length === 0 ? (
          <p className="col-span-full text-center text-gray-500">No experiences added yet.</p>
        ) : (
          experiences.map(exp => (
            <div key={exp._id} className="bg-white p-4 shadow-lg rounded-lg">
              <ExperienceCard exp={exp} role="organization" />
              <div className="flex justify-end mt-4">
                <button onClick={() => handleEdit(exp)} className="bg-yellow-500 text-white p-2 mr-2 rounded hover:bg-yellow-600">Edit</button>
                <button onClick={() => handleDelete(exp._id)} className="bg-red-500 text-white p-2 rounded hover:bg-red-600">Delete</button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default OrgDashboard;
