import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useLocation } from 'react-router-dom';
import jwtDecode from 'jwt-decode';
import Login from './pages/Login';
import Register from './pages/Register';
import StudentDashboard from './components/StudentDashboard';
import OrgDashboard from './components/OrgDashboard';
import PublicPortfolio from './components/PublicPortfolio';

const AppContent = () => {
  const [token, setToken] = useState(localStorage.getItem('token'));
  const logout = () => {
    localStorage.removeItem('token');
    setToken(null);
  };

  useEffect(() => {
    const storedToken = localStorage.getItem('token');
    if (storedToken) {
      try {
        const decoded = jwtDecode(storedToken);
        // Redirect based on role if already logged in
        const location = useLocation();
        if (!location.pathname.includes('/dashboard') && decoded.role) {
          setToken(storedToken);
        }
      } catch (err) {
        localStorage.removeItem('token');
      }
    }
  }, []);

  return (
    <div className="min-h-screen bg-gray-100">
      <Routes>
        <Route path="/login" element={<Login setToken={setToken} />} />
        <Route path="/register" element={<Register setToken={setToken} />} />
        <Route
          path="/student-dashboard"
          element={token && jwtDecode(token).role === 'student' ? <StudentDashboard token={token} logout={logout} /> : <Navigate to="/login" replace />}
        />
        <Route
          path="/org-dashboard"
          element={token && jwtDecode(token).role === 'organization' ? <OrgDashboard token={token} logout={logout} /> : <Navigate to="/login" replace />}
        />
        <Route path="/portfolio/:username" element={<PublicPortfolio />} />
        <Route path="/" element={<Navigate to="/login" replace />} />
      </Routes>
    </div>
  );
};

function App() {
  return (
    <Router>
      <AppContent />
    </Router>
  );
}

export default App;
