import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

const Register = () => {
  const [role, setRole] = useState('student');
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.post('/auth/register', { role, name, email, password }); // Corrected path
      navigate('/login');
    } catch (err) {
      alert('Registration failed: ' + err.message);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="max-w-md mx-auto p-4">
      <select value={role} onChange={e => setRole(e.target.value)} className="border p-2 w-full mb-2">
        <option value="student">Student</option>
        <option value="organization">Organization</option>
      </select>
      <input type="text" value={name} onChange={e => setName(e.target.value)} placeholder="Name" className="border p-2 w-full mb-2" />
      <input type="email" value={email} onChange={e => setEmail(e.target.value)} placeholder="Email" className="border p-2 w-full mb-2" />
      <input type="password" value={password} onChange={e => setPassword(e.target.value)} placeholder="Password" className="border p-2 w-full mb-2" />
      <button type="submit" className="bg-green-500 text-white p-2 w-full">Register</button>
    </form>
  );
};

export default Register;
