import React, { useEffect, useState } from 'react';
import axios from 'axios';
import ExperienceCard from './ExperienceCard';

const OrgDashboard = ({ token, logout }) => {
  const [formData, setFormData] = useState({ activityName: '', achievementLevel: '', description: '', date: '', studentEmail: '', category: '' });
  const [files, setFiles] = useState([]);
  const [experiences, setExperiences] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [loading, setLoading] = useState(true);
  const [modalOpen, setModalOpen] = useState(false);
  const [viewModalOpen, setViewModalOpen] = useState(false);
  const [selectedExp, setSelectedExp] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('All Statuses');
  const [activityFilter, setActivityFilter] = useState('All Activities');

  useEffect(() => {
    const fetchExperiences = async () => {
      setLoading(true);
      const config = { headers: { Authorization: `Bearer ${token}` } };
      try {
        const res = await axios.get('/api/experience', config);
        setExperiences(res.data);
      } catch (err) {
        alert('Error fetching: ' + (err.response?.data?.error || 'Unknown error'));
      } finally {
        setLoading(false);
      }
    };
    fetchExperiences();
  }, [token]);

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleFileChange = (e) => setFiles(e.target.files);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    Object.keys(formData).forEach(key => data.append(key, formData[key]));
    Array.from(files).forEach(file => data.append('documents', file));
    const config = { headers: { Authorization: `Bearer ${token}`, 'Content-Type': 'multipart/form-data' } };
    try {
      if (editingId) {
        await axios.patch(`/api/experience/${editingId}`, data, config);
        setEditingId(null);
      } else {
        await axios.post('/api/experience', data, config);
      }
      const res = await axios.get('/api/experience', { headers: { Authorization: `Bearer ${token}` } });
      setExperiences(res.data);
      setFormData({ activityName: '', achievementLevel: '', description: '', date: '', studentEmail: '', category: '' });
      setFiles([]);
      setModalOpen(false);
    } catch (err) {
      alert('Error: ' + (err.response?.data?.error || 'Unknown error'));
    }
  };

  const handleEdit = (exp) => {
    setFormData({
      activityName: exp.activityName,
      achievementLevel: exp.achievementLevel,
      description: exp.description,
      date: exp.date.split('T')[0],
      studentEmail: exp.studentEmail,
      category: exp.category,
    });
    setEditingId(exp._id);
    setModalOpen(true);
  };

  const handleDelete = async (id) => {
    const config = { headers: { Authorization: `Bearer ${token}` } };
    try {
      await axios.delete(`/api/experience/${id}`, config);
      setExperiences(prev => prev.filter(exp => exp._id !== id));
    } catch (err) {
      alert('Error: ' + (err.response?.data?.error || 'Unknown error'));
    }
  };

  const handleApprove = async (id) => {
    const config = { headers: { Authorization: `Bearer ${token}` } };
    try {
      await axios.post(`/api/experience/${id}/approve`, {}, config);
      const res = await axios.get('/api/experience', config);
      setExperiences(res.data);
    } catch (err) {
      alert('Error: ' + (err.response?.data?.error || 'Unknown error'));
    }
  };

  const handleReject = async (id) => {
    const config = { headers: { Authorization: `Bearer ${token}` } };
    try {
      await axios.post(`/api/experience/${id}/reject`, {}, config);
      const res = await axios.get('/api/experience', config);
      setExperiences(res.data);
    } catch (err) {
      alert('Error: ' + (err.response?.data?.error || 'Unknown error'));
    }
  };

  const handleView = (exp) => {
    setSelectedExp(exp);
    setViewModalOpen(true);
  };

  const filteredExperiences = experiences.filter(exp => {
    const matchesSearch = exp.studentEmail.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = statusFilter === 'All Statuses' || exp.status === statusFilter;
    const matchesActivity = activityFilter === 'All Activities' || exp.category === activityFilter;
    return matchesSearch && matchesStatus && matchesActivity;
  });

  const uniqueStudents = new Set(experiences.map(exp => exp.studentEmail)).size;
  const pendingCount = experiences.filter(exp => exp.status === 'PENDING').length;
  const approvedCount = experiences.filter(exp => exp.status === 'APPROVED').length;

  if (loading) return <div className="flex justify-center items-center h-screen"><div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500"></div></div>;

  return (
    <div className="p-6 max-w-7xl mx-auto">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold text-gray-900">Organization Dashboard</h1>
        <button onClick={logout} className="text-red-600 hover:text-red-800">
          Logout
        </button>
      </div>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4 mb-8">
        <div className="bg-white p-4 rounded-lg shadow">
          <h3 className="text-lg font-semibold text-gray-600">Total Students</h3>
          <p className="text-3xl font-bold">{uniqueStudents}</p>
        </div>
        <div className="bg-white p-4 rounded-lg shadow">
          <h3 className="text-lg font-semibold text-gray-600">Experiences Added</h3>
          <p className="text-3xl font-bold">{experiences.length}</p>
        </div>
        <div className="bg-white p-4 rounded-lg shadow">
          <h3 className="text-lg font-semibold text-gray-600">Pending</h3>
          <p className="text-3xl font-bold">{pendingCount}</p>
        </div>
        <div className="bg-white p-4 rounded-lg shadow">
          <h3 className="text-lg font-semibold text-gray-600">Verified</h3>
          <p className="text-3xl font-bold">{approvedCount}</p>
        </div>
      </div>
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-2xl font-semibold text-gray-800">Experience Management</h2>
        <button onClick={() => setModalOpen(true)} className="bg-blue-500 text-white p-2 rounded hover:bg-blue-600">
          + Add Experience
        </button>
      </div>
      <div className="flex gap-4 mb-4">
        <select value={statusFilter} onChange={e => setStatusFilter(e.target.value)} className="border p-2 rounded">
          <option>All Statuses</option>
          <option>PENDING</option>
          <option>APPROVED</option>
          <option>REJECTED</option>
        </select>
        <input value={searchTerm} onChange={e => setSearchTerm(e.target.value)} placeholder="Search students..." className="border p-2 rounded flex-1" />
        <select value={activityFilter} onChange={e => setActivityFilter(e.target.value)} className="border p-2 rounded">
          <option>All Activities</option>
          <option>Course</option>
          <option>Competition</option>
          <option>Workshop</option>
        </select>
      </div>
      <div className="space-y-4">
        {filteredExperiences.length === 0 ? (
          <p className="text-center text-gray-500">No experiences match the filters.</p>
        ) : (
          filteredExperiences.map(exp => (
            <div key={exp._id} className="bg-white p-4 rounded-lg shadow flex justify-between items-center">
              <div>
                <h3 className="text-lg font-semibold">{exp.activityName} <span className={`ml-2 px-2 py-1 text-sm rounded ${exp.status === 'APPROVED' ? 'bg-green-100 text-green-800' : exp.status === 'REJECTED' ? 'bg-red-100 text-red-800' : 'bg-blue-100 text-blue-800'}`}>{exp.status}</span></h3>
                <p className="text-gray-600">{exp.studentEmail}</p>
                <p className="text-gray-500">{exp.description}</p>
                <p className="text-gray-400 text-sm">{exp.date.split('T')[0]} {exp.category}</p>
              </div>
              <div className="flex gap-2">
                <button onClick={() => handleApprove(exp._id)} className="text-green-500">✔</button>
                <button onClick={() => handleReject(exp._id)} className="text-red-500">✗</button>
                <button onClick={() => handleView(exp)} className="text-blue-500">○</button>
              </div>
            </div>
          ))
        )}
      </div>

      {/* Add/Edit Modal */}
      {modalOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg max-w-md w-full">
            <h2 className="text-xl font-bold mb-4">Add Experience</h2>
            <form onSubmit={handleSubmit} className="space-y-4">
              <input name="studentEmail" value={formData.studentEmail} onChange={handleChange} placeholder="Student Email" className="w-full border p-2 rounded" required />
              <input type="date" name="date" value={formData.date} onChange={handleChange} className="w-full border p-2 rounded" required />
              <input name="activityName" value={formData.activityName} onChange={handleChange} placeholder="Activity Name" className="w-full border p-2 rounded" required />
              <textarea name="description" value={formData.description} onChange={handleChange} placeholder="Description" className="w-full border p-2 rounded" required />
              <select name="achievementLevel" value={formData.achievementLevel} onChange={handleChange} className="w-full border p-2 rounded" required>
                <option value="">Select Achievement Level</option>
                <option>Winner</option>
                <option>Participant</option>
                <option>Certificate</option>
              </select>
              <select name="category" value={formData.category} onChange={handleChange} className="w-full border p-2 rounded" required>
                <option value="">Select Category</option>
                <option>Course</option>
                <option>Competition</option>
                <option>Workshop</option>
              </select>
              <input type="file" multiple onChange={handleFileChange} />
              <div className="flex justify-end gap-2">
                <button type="submit" className="bg-blue-500 text-white p-2 rounded">Add Experience</button>
                <button type="button" onClick={() => setModalOpen(false)} className="bg-gray-500 text-white p-2 rounded">Cancel</button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* View Modal */}
      {viewModalOpen && selectedExp && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg max-w-md w-full">
            <h2 className="text-xl font-bold mb-4">{selectedExp.activityName}</h2>
            <p><strong>Student:</strong> {selectedExp.studentEmail}</p>
            <p><strong>Description:</strong> {selectedExp.description}</p>
            <p><strong>Achievement Level:</strong> {selectedExp.achievementLevel}</p>
            <p><strong>Category:</strong> {selectedExp.category}</p>
            <p><strong>Date:</strong> {new Date(selectedExp.date).toLocaleDateString()}</p>
            <p><strong>Documents:</strong></p>
            {selectedExp.documents.map((doc, idx) => (
              <a key={idx} href={`/api/documents/${doc.split('/').pop()}`} target="_blank" rel="noopener noreferrer" className="block text-blue-500">Document {idx + 1}</a>
            ))}
            <button onClick={() => setViewModalOpen(false)} className="mt-4 bg-red-500 text-white p-2 w-full rounded">Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default OrgDashboard;
