import React, { useState } from 'react';
import Modal from './Modal';

const ExperienceCard = ({ exp, onAccept, onDecline, onToggleFeatured, role }) => {
  const [showDetails, setShowDetails] = useState(false);

  return (
    <div className="bg-white p-4 shadow rounded">
      <h2 className="text-xl font-bold">{exp.activityName}</h2>
      <p className="text-gray-600">{new Date(exp.date).toLocaleDateString()}</p>
      <p>{exp.description}</p>
      <span className={`inline-block px-2 py-1 text-sm rounded ${exp.status === 'APPROVED' ? 'bg-green-200 text-green-800' : exp.status === 'REJECTED' ? 'bg-red-200 text-red-800' : 'bg-yellow-200 text-yellow-800'}`}>
        {exp.status}
      </span>
      {role === 'student' && exp.status === 'PENDING' && (
        <div className="mt-2">
          <button onClick={() => onAccept(exp._id)} className="bg-green-500 text-white p-1 mr-2 rounded hover:bg-green-600">Accept</button>
          <button onClick={() => onDecline(exp._id)} className="bg-red-500 text-white p-1 rounded hover:bg-red-600">Decline</button>
        </div>
      )}
      {role === 'student' && (
        <button onClick={() => onToggleFeatured(exp._id)} className="mt-2 text-yellow-500 hover:text-yellow-600">
          {exp.featured ? 'Unstar' : 'Star'}
        </button>
      )}
      <button onClick={() => setShowDetails(true)} className="mt-2 text-blue-500 hover:underline">Learn More</button>
      <div className="mt-2">
        {exp.documents.map((doc, idx) => (
          <a key={idx} href={`/api/documents/${doc.split('/').pop()}`} target="_blank" rel="noopener noreferrer" className="block text-blue-500 hover:underline">View Document {idx + 1}</a>
        ))}
      </div>
      <Modal isOpen={showDetails} onClose={() => setShowDetails(false)} title={exp.activityName}>
        <p>{exp.description}</p>
        <p>Achievement Level: {exp.achievementLevel}</p>
        <p>Date: {new Date(exp.date).toLocaleDateString()}</p>
        <p>Organization: {exp.orgId?.name}</p>
      </Modal>
    </div>
  );
};

export default ExperienceCard;
