import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import jwtDecode from 'jwt-decode';

const Register = ({ setToken }) => {
  const [formData, setFormData] = useState({ role: 'student', name: '', email: '', password: '' });
  const navigate = useNavigate();

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('/api/auth/register', formData);
      localStorage.setItem('token', res.data.token);
      setToken(res.data.token);
      const { role } = jwtDecode(res.data.token);
      navigate(role === 'student' ? '/student-dashboard' : '/org-dashboard');
    } catch (err) {
      alert('Registration failed: ' + (err.response?.data?.error || 'Unknown error'));
    }
  };

  return (
    <div className="max-w-md mx-auto p-6 bg-white shadow-lg rounded-lg mt-10">
      <h2 className="text-2xl font-bold text-center mb-6 text-gray-900">Register</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <select name="role" value={formData.role} onChange={handleChange} className="w-full p-2 border rounded focus:ring-2 focus:ring-blue-500" required>
          <option value="student">Student</option>
          <option value="organization">Organization</option>
        </select>
        <input type="text" name="name" value={formData.name} onChange={handleChange} placeholder="Name" className="w-full p-2 border rounded focus:ring-2 focus:ring-blue-500" required />
        <input type="email" name="email" value={formData.email} onChange={handleChange} placeholder="Email" className="w-full p-2 border rounded focus:ring-2 focus:ring-blue-500" required />
        <input type="password" name="password" value={formData.password} onChange={handleChange} placeholder="Password" className="w-full p-2 border rounded focus:ring-2 focus:ring-blue-500" required />
        <button type="submit" className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600 transition">
          Register
        </button>
      </form>
      <p className="mt-4 text-center text-gray-600">
        Already have an account? <Link to="/login" className="text-blue-500 hover:underline">Login</Link>
      </p>
    </div>
  );
};

export default Register;
