import React, { useState } from 'react';
import axios from 'axios';

const PortfolioBuilder = ({ user, token, setUser }) => {
  const [profile, setProfile] = useState(user.profile || { bio: '', skills: [], links: {} });

  const handleChange = (e) => {
    const [section, key] = e.target.name.split('.');
    if (section === 'links') {
      setProfile({ ...profile, links: { ...profile.links, [key]: e.target.value } });
    } else if (key === 'skills') {
      setProfile({ ...profile, skills: e.target.value.split(',').map(s => s.trim()) });
    } else {
      setProfile({ ...profile, [key]: e.target.value });
    }
  };

  const handleUpdate = async () => {
    const config = { headers: { Authorization: `Bearer ${token}` } };
    try {
      const res = await axios.patch('/api/user/profile', profile, config);
      setUser({ ...user, profile: res.data });
      alert('Profile updated');
    } catch (err) {
      alert('Error updating profile: ' + (err.response?.data?.error || 'Unknown error'));
    }
  };

  const handlePublish = async () => {
    const username = prompt('Enter unique username for public URL');
    if (!username) return;
    const config = { headers: { Authorization: `Bearer ${token}` } };
    try {
      const res = await axios.post('/api/portfolio/publish', { username }, config);
      setUser({ ...user, profile: { ...user.profile, publicUrl: res.data.publicUrl } });
      alert(`Public URL: ${window.location.origin}${res.data.publicUrl}`);
    } catch (err) {
      alert('Error publishing: ' + (err.response?.data?.error || 'Unknown error'));
    }
  };

  return (
    <div className="bg-white p-4 shadow rounded mb-4">
      <h2 className="text-xl mb-2">Portfolio Builder</h2>
      <textarea name="bio" value={profile.bio} onChange={handleChange} placeholder="Bio" className="border p-2 w-full mb-2 rounded" />
      <input name="skills" value={profile.skills.join(', ')} onChange={handleChange} placeholder="Skills (comma separated)" className="border p-2 w-full mb-2 rounded" />
      <input name="links.github" value={profile.links.github} onChange={handleChange} placeholder="GitHub URL" className="border p-2 w-full mb-2 rounded" />
      <input name="links.leetcode" value={profile.links.leetcode} onChange={handleChange} placeholder="LeetCode URL" className="border p-2 w-full mb-2 rounded" />
      <input name="links.figma" value={profile.links.figma} onChange={handleChange} placeholder="Figma URL" className="border p-2 w-full mb-2 rounded" />
      <input name="links.linkedin" value={profile.links.linkedin} onChange={handleChange} placeholder="LinkedIn URL" className="border p-2 w-full mb-2 rounded" />
      <button onClick={handleUpdate} className="bg-blue-500 text-white p-2 mr-2 rounded hover:bg-blue-600">Update Profile</button>
      <button onClick={handlePublish} className="bg-purple-500 text-white p-2 rounded hover:bg-purple-600">Publish Portfolio</button>
    </div>
  );
};

export default PortfolioBuilder;
