import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import api from '../services/api';

function OrgDashboard() {
  const [studentEmail, setStudentEmail] = useState('');
  const [activityName, setActivityName] = useState('');
  const [achievementLevel, setAchievementLevel] = useState('');
  const [description, setDescription] = useState('');
  const [date, setDate] = useState('');
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('studentEmail', studentEmail);
    formData.append('activityName', activityName);
    formData.append('achievementLevel', achievementLevel);
    formData.append('description', description);
    formData.append('date', date);

    try {
      await api.post('/api/experience', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      alert('Experience submitted');
      setStudentEmail('');
      setActivityName('');
      setAchievementLevel('');
      setDescription('');
      setDate('');
    } catch (err) {
      alert('Failed to submit experience: ' + err.message);
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Organization Dashboard - {user.name}</h2>
      <button onClick={handleLogout} className="mb-4 bg-red-500 text-white p-2 rounded hover:bg-red-600">
        Logout
      </button>
      <form onSubmit={handleSubmit} className="bg-white p-6 rounded shadow-md">
        <h3 className="text-xl mb-2">Add Experience</h3>
        <div className="mb-4">
          <label className="block text-gray-700">Student Email</label>
          <input
            type="email"
            value={studentEmail}
            onChange={(e) => setStudentEmail(e.target.value)}
            className="w-full p-2 border rounded"
            required
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700">Activity Name</label>
          <input
            type="text"
            value={activityName}
            onChange={(e) => setActivityName(e.target.value)}
            className="w-full p-2 border rounded"
            required
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700">Achievement Level</label>
          <input
            type="text"
            value={achievementLevel}
            onChange={(e) => setAchievementLevel(e.target.value)}
            className="w-full p-2 border rounded"
            required
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700">Description</label>
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            className="w-full p-2 border rounded"
            required
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700">Date</label>
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            className="w-full p-2 border rounded"
            required
          />
        </div>
        <button type="submit" className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600">
          Submit
        </button>
      </form>
    </div>
  );
}

export default OrgDashboard;
