import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const StudentDashboard = ({ token, logout }) => {
  const [user, setUser] = useState(null);
  const [experiences, setExperiences] = useState([]);
  const [pendingExp, setPendingExp] = useState([]);
  const [profile, setProfile] = useState({ 
    bio: '', 
    skills: [], 
    github: '', 
    leetcode: '', 
    figma: '', 
    linkedin: '', 
    customUrl: '', 
    isPublic: false 
  });
  const [profilePic, setProfilePic] = useState(null); // New state for profile picture file
  const [preview, setPreview] = useState(null); // New state for live preview
  const [loading, setLoading] = useState(true);
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [expandedExp, setExpandedExp] = useState(null); // For expandable details
  const [inputValue, setInputValue] = useState(""); // New state for skills input

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      const config = { headers: { Authorization: `Bearer ${token}` } };
      try {
        const [userRes, expRes] = await Promise.all([
          axios.get('/api/user/me', config),
          axios.get('/api/experience', config),
        ]);
        setUser(userRes.data);
        const allExp = expRes.data;
        setExperiences(allExp.filter(exp => exp.status === 'verified'));
        setPendingExp(allExp.filter(exp => exp.status === 'pending'));
        setProfile({
          bio: userRes.data.profile.bio || '',
          skills: userRes.data.profile.skills || [],
          github: userRes.data.profile.links.github || '',
          leetcode: userRes.data.profile.links.leetcode || '',
          figma: userRes.data.profile.links.figma || '',
          linkedin: userRes.data.profile.links.linkedin || '',
          customUrl: userRes.data.profile.publicUrl || '',
          isPublic: userRes.data.profile.publicUrl ? true : false,
        });
        // Set initial preview if profile picture exists (assuming backend returns it)
        if (userRes.data.profile.picture) setPreview(`/api/documents/${userRes.data.profile.picture}`);
      } catch (err) {
        alert('Error fetching data: ' + (err.response?.data?.error || 'Unknown error'));
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [token]);

  const handleAction = async (id, action) => {
    const config = { headers: { Authorization: `Bearer ${token}` } };
    try {
      await axios.post(`/api/experience/${id}/${action}`, {}, config);
      const res = await axios.get('/api/experience', config);
      setExperiences(res.data.filter(exp => exp.status === 'verified'));
      setPendingExp(res.data.filter(exp => exp.status === 'pending'));
    } catch (err) {
      alert('Error: ' + (err.response?.data?.error || 'Unknown error'));
    }
  };

  const handleProfileUpdate = async (e) => {
    e.preventDefault();
    const config = { headers: { Authorization: `Bearer ${token}`, 'Content-Type': 'multipart/form-data' } };
    const formData = new FormData();
    formData.append('profile', JSON.stringify({
      bio: profile.bio,
      skills: profile.skills,
      links: { github: profile.github, leetcode: profile.leetcode, figma: profile.figma, linkedin: profile.linkedin },
      publicUrl: profile.customUrl,
    }));
    if (profilePic) formData.append('picture', profilePic);

    try {
      await axios.put('/api/user/me', formData, config);
      alert('Profile updated successfully');
    } catch (err) {
      alert('Error: ' + (err.response?.data?.error || 'Unknown error'));
    }
  };

  const handleSkillKeyDown = (e) => {
    if (e.key === 'Enter' && inputValue.trim()) {
      e.preventDefault();
      if (!profile.skills.includes(inputValue.trim())) {
        setProfile(prev => ({ ...prev, skills: [...prev.skills, inputValue.trim()] }));
      }
      setInputValue('');
    }
  };

  const removeSkill = (skillToRemove) => {
    setProfile(prev => ({ ...prev, skills: prev.skills.filter(skill => skill !== skillToRemove) }));
  };

  const handlePicChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setProfilePic(file);
      setPreview(URL.createObjectURL(file)); // For live preview
    }
  };

  const publishPortfolio = async () => {
    const config = { headers: { Authorization: `Bearer ${token}` } };
    try {
      const res = await axios.post('/api/portfolio/publish', {}, config);
      setProfile(prev => ({ ...prev, customUrl: res.data.publicUrl, isPublic: true }));
      alert('Portfolio published!');
    } catch (err) {
      alert('Error: ' + (err.response?.data?.error || 'Unknown error'));
    }
  };

  const toggleFeatured = async (id) => {
    const config = { headers: { Authorization: `Bearer ${token}` } };
    try {
      const exp = experiences.find(e => e._id === id) || pendingExp.find(e => e._id === id);
      if (exp) {
        await axios.patch(`/api/experience/${id}`, { featured: !exp.featured }, config);
        const res = await axios.get('/api/experience', config);
        setExperiences(res.data.filter(exp => exp.status === 'verified'));
        setPendingExp(res.data.filter(exp => exp.status === 'pending'));
      }
    } catch (err) {
      alert('Error: ' + (err.response?.data?.error || 'Unknown error'));
    }
  };

  if (loading) return <div className="flex justify-center items-center h-screen"><div className="animate-spin rounded-full h-12 w-12 border-t-4 border-b-4 border-blue-500"></div></div>;

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow p-4 flex justify-between items-center sticky top-0 z-10">
        <h1 className="text-2xl font-bold text-blue-600">ExperienceHub</h1>
        <div className="relative">
          <button onClick={() => setDropdownOpen(!dropdownOpen)} className="flex items-center space-x-2">
            <span className="text-gray-700">{user?.name}</span>
            <svg className="w-5 h-5 text-gray-500" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M5.293 7.293a1 1 0 011.414 0L10 10.586l3.293-3.293a1 1 0 111.414 1.414l-4 4a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414z" clipRule="evenodd" />
            </svg>
          </button>
          {dropdownOpen && (
            <div className="absolute right-0 mt-2 w-40 bg-white border rounded shadow">
              <button onClick={logout} className="w-full px-4 py-2 text-red-600 hover:bg-gray-100">Logout</button>
            </div>
          )}
        </div>
      </header>

      {/* Main Layout */}
      <div className="max-w-7xl mx-auto p-6 grid grid-cols-1 lg:grid-cols-4 gap-6">
        {/* Left/Main Content */}
        <div className="lg:col-span-3 space-y-8">
          {/* Stats Row */}
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
            <div className="bg-gradient-to-r from-blue-400 to-blue-600 p-6 rounded-lg shadow-lg text-white">
              <h3 className="text-sm font-semibold">Total Experiences</h3>
              <p className="text-3xl font-bold">{experiences.length}</p>
            </div>
            <div className="bg-gradient-to-r from-green-400 to-green-600 p-6 rounded-lg shadow-lg text-white">
              <h3 className="text-sm font-semibold">Pending Review</h3>
              <p className="text-3xl font-bold">{pendingExp.length}</p>
            </div>
            <div className="bg-gradient-to-r from-purple-400 to-purple-600 p-6 rounded-lg shadow-lg text-white">
              <h3 className="text-sm font-semibold">Achievements</h3>
              <p className="text-3xl font-bold">{experiences.filter(exp => exp.achievementLevel === 'winner').length}</p>
            </div>
            <div className="bg-gradient-to-r from-yellow-400 to-yellow-600 p-6 rounded-lg shadow-lg text-white">
              <h3 className="text-sm font-semibold">Profile Views</h3>
              <p className="text-3xl font-bold">0</p>
            </div>
          </div>

          {/* Pending Experiences */}
          <section>
            <h2 className="text-2xl font-semibold text-gray-800 mb-4">Pending Experiences</h2>
            {pendingExp.length === 0 ? (
              <p className="text-gray-500">No pending experiences.</p>
            ) : (
              <div className="space-y-4">
                {pendingExp.map(exp => (
                  <div key={exp._id} className="bg-white p-4 rounded-lg shadow hover:shadow-lg">
                    <h3 className="text-lg font-semibold">{exp.activityName}</h3>
                    <p className="text-gray-600">Submitted by: {exp.orgId.name || 'Unknown Org'}</p>
                    <p className="text-gray-500 text-sm">{exp.description}</p>
                    <p className="text-gray-400 text-xs">{new Date(exp.date).toLocaleDateString()} | {exp.achievementLevel}</p>
                    <div className="mt-3 flex gap-2">
                      <button onClick={() => handleAction(exp._id, 'accept')} className="bg-blue-500 text-white px-3 py-1 rounded">Accept</button>
                      <button onClick={() => handleAction(exp._id, 'decline')} className="bg-red-500 text-white px-3 py-1 rounded">Decline</button>
                      <button onClick={() => setExpandedExp(exp._id)} className="border px-3 py-1 rounded">View Details</button>
                    </div>
                    {expandedExp === exp._id && (
                      <div className="mt-2 bg-gray-50 p-2 rounded">
                        <p><strong>Description:</strong> {exp.description}</p>
                        <p><strong>Docs:</strong> {exp.documents?.join(', ') || 'None'}</p>
                        <button onClick={() => setExpandedExp(null)} className="text-red-500 mt-2">Close</button>
                      </div>
                    )}
                  </div>
                ))}
              </div>
            )}
          </section>

          {/* My Experiences */}
          <section>
            <h2 className="text-2xl font-semibold text-gray-800 mb-4">My Experiences</h2>
            <div className="space-y-4">
              {experiences.map(exp => (
                <div key={exp._id} className="bg-white p-4 rounded-lg shadow hover:shadow-lg">
                  <div className="flex justify-between">
                    <div>
                      <h3 className="font-semibold text-lg">{exp.activityName}</h3>
                      <p className="text-gray-600">Org: {exp.orgId.name || 'Unknown Org'}</p>
                      <p className="text-gray-500 text-sm">{exp.description}</p>
                    </div>
                    <button onClick={() => toggleFeatured(exp._id)} className="text-yellow-500 text-2xl">{exp.featured ? '★' : '☆'}</button>
                  </div>
                  <div className="mt-2 flex gap-2">
                    <span className="px-2 py-1 bg-green-100 text-green-800 text-xs rounded-full">Verified</span>
                    {exp.featured && <span className="px-2 py-1 bg-yellow-100 text-yellow-800 text-xs rounded-full">Featured</span>}
                  </div>
                </div>
              ))}
            </div>
          </section>
        </div>

        {/* Right Sidebar */}
        <aside className="space-y-6">
          {/* Profile */}
          <div className="bg-white p-4 rounded-lg shadow">
            <div className="flex flex-col items-center">
              <label htmlFor="profilePic" className="cursor-pointer">
                <img
                  src={preview || "/default-avatar.png"}
                  alt="Profile"
                  className="w-24 h-24 rounded-full object-cover border"
                />
              </label>
              <input
                type="file"
                id="profilePic"
                accept="image/*"
                onChange={handlePicChange}
                className="hidden"
              />
            </div>
            <h3 className="text-xl text-center font-semibold">{user?.name}</h3>
            <p className="text-gray-600 text-center">{user?.email}</p>
            <textarea
              value={profile.bio}
              onChange={(e) => setProfile({ ...profile, bio: e.target.value })}
              className="w-full border mt-3 p-2 rounded"
            />
            {/* Skills */}
            <div className="mt-3">
              <label className="block text-sm font-medium">Skills</label>
              <div className="flex flex-wrap gap-2 mt-2">
                {profile.skills.map((skill, index) => (
                  <span
                    key={index}
                    className="px-2 py-1 bg-blue-100 text-blue-700 rounded-lg text-sm flex items-center"
                  >
                    {skill}
                    <button
                      type="button"
                      onClick={() => removeSkill(skill)}
                      className="ml-1 text-red-500 font-bold"
                    >
                      ×
                    </button>
                  </span>
                ))}
              </div>
              <input
                type="text"
                placeholder="Add skill..."
                value={inputValue}
                onChange={(e) => setInputValue(e.target.value)}
                onKeyDown={handleSkillKeyDown}
                className="mt-2 border rounded-md px-2 py-1 w-full"
              />
            </div>
            {/* Links */}
            <div className="mt-4 space-y-2">
              <input 
                value={profile.github} 
                onChange={(e) => setProfile({ ...profile, github: e.target.value })} 
                placeholder="GitHub URL" 
                className="w-full border p-2 rounded" 
              />
              <input 
                value={profile.leetcode} 
                onChange={(e) => setProfile({ ...profile, leetcode: e.target.value })} 
                placeholder="LeetCode URL" 
                className="w-full border p-2 rounded" 
              />
              <input 
                value={profile.figma} 
                onChange={(e) => setProfile({ ...profile, figma: e.target.value })} 
                placeholder="Figma URL" 
                className="w-full border p-2 rounded" 
              />
              <input 
                value={profile.linkedin} 
                onChange={(e) => setProfile({ ...profile, linkedin: e.target.value })} 
                placeholder="LinkedIn URL" 
                className="w-full border p-2 rounded" 
              />
            </div>
            <button onClick={handleProfileUpdate} className="w-full bg-blue-500 text-white px-4 py-2 rounded mt-4">Update Profile</button>
          </div>

          {/* Portfolio Settings */}
          <div className="bg-white p-4 rounded-lg shadow">
            <h4 className="font-semibold text-lg">Portfolio Settings</h4>
            <input 
              value={profile.customUrl} 
              onChange={(e) => setProfile({ ...profile, customUrl: e.target.value })} 
              placeholder="Custom Portfolio URL" 
              className="w-full border p-2 rounded mt-2" 
              disabled 
            />
            <div className="mt-2 flex items-center">
              <input 
                type="checkbox" 
                checked={profile.isPublic} 
                onChange={(e) => setProfile({ ...profile, isPublic: e.target.checked })} 
                className="mr-2" 
              />
              <span>Make Portfolio Public</span>
            </div>
            <button onClick={publishPortfolio} className="w-full bg-green-500 text-white px-4 py-2 rounded mt-4">Publish Portfolio</button>
            {profile.isPublic && <Link to={`/portfolio/${user?.email.split('@')[0]}`} className="w-full block text-center bg-blue-500 text-white px-4 py-2 rounded mt-2">View Public Portfolio</Link>}
          </div>
        </aside>
      </div>
    </div>
  );
};

export default StudentDashboard;
