import React, { useState } from 'react';
import api from '../services/api';

const OrgDashboard = () => {
  const [studentEmail, setStudentEmail] = useState('');
  const [activityName, setActivityName] = useState('');
  const [achievementLevel, setAchievementLevel] = useState('');
  const [description, setDescription] = useState('');
  const [date, setDate] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.post('/experience', { studentEmail, activityName, achievementLevel, description, date });
      alert('Experience added');
    } catch (err) {
      alert('Failed to add experience');
    }
  };

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Organization Dashboard</h2>
      <form onSubmit={handleSubmit} className="max-w-md mx-auto">
        <input type="text" value={studentEmail} onChange={e => setStudentEmail(e.target.value)} placeholder="Student Email" className="border p-2 w-full mb-2" />
        <input type="text" value={activityName} onChange={e => setActivityName(e.target.value)} placeholder="Activity Name" className="border p-2 w-full mb-2" />
        <input type="text" value={achievementLevel} onChange={e => setAchievementLevel(e.target.value)} placeholder="Achievement Level" className="border p-2 w-full mb-2" />
        <textarea value={description} onChange={e => setDescription(e.target.value)} placeholder="Description" className="border p-2 w-full mb-2" />
        <input type="date" value={date} onChange={e => setDate(e.target.value)} className="border p-2 w-full mb-2" />
        <button type="submit" className="bg-blue-500 text-white p-2 w-full">Add Experience</button>
      </form>
    </div>
  );
};

export default OrgDashboard;
