import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const StudentDashboard = ({ token, logout }) => {
  const [user, setUser] = useState(null);
  const [experiences, setExperiences] = useState([]);
  const [pendingExp, setPendingExp] = useState([]);
  const [profile, setProfile] = useState({ bio: '', skills: [], github: '', linkedin: '', customUrl: '', isPublic: false });
  const [loading, setLoading] = useState(true);
  const [dropdownOpen, setDropdownOpen] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      const config = { headers: { Authorization: `Bearer ${token}` } };
      try {
        const [userRes, expRes] = await Promise.all([
          axios.get('/api/user/me', config),
          axios.get('/api/experience/student', config),
        ]);
        setUser(userRes.data);
        const allExp = expRes.data;
        setExperiences(allExp.filter(exp => exp.status === 'APPROVED'));
        setPendingExp(allExp.filter(exp => exp.status === 'PENDING'));
        setProfile({
          bio: userRes.data.bio || '',
          skills: userRes.data.skills || [],
          github: userRes.data.github || '',
          linkedin: userRes.data.linkedin || '',
          customUrl: userRes.data.customUrl || '',
          isPublic: userRes.data.isPublic || false,
        });
      } catch (err) {
        alert('Error fetching data: ' + (err.response?.data?.error || 'Unknown error'));
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [token]);

  const handleAction = async (id, action) => {
    const config = { headers: { Authorization: `Bearer ${token}` } };
    try {
      await axios.post(`/api/experience/${id}/${action}`, {}, config);
      const res = await axios.get('/api/experience/student', config);
      setExperiences(res.data.filter(exp => exp.status === 'APPROVED'));
      setPendingExp(res.data.filter(exp => exp.status === 'PENDING'));
    } catch (err) {
      alert('Error: ' + (err.response?.data?.error || 'Unknown error'));
    }
  };

  const handleProfileUpdate = async (e) => {
    e.preventDefault();
    const config = { headers: { Authorization: `Bearer ${token}` } };
    try {
      await axios.put('/api/user/me', profile, config);
      alert('Profile updated successfully');
    } catch (err) {
      alert('Error: ' + (err.response?.data?.error || 'Unknown error'));
    }
  };

  const addSkill = (e) => {
    if (e.key === 'Enter' && e.target.value) {
      setProfile(prev => ({ ...prev, skills: [...prev.skills, e.target.value] }));
      e.target.value = '';
    }
  };

  const removeSkill = (skillToRemove) => {
    setProfile(prev => ({ ...prev, skills: prev.skills.filter(skill => skill !== skillToRemove) }));
  };

  if (loading) return <div className="flex justify-center items-center h-screen"><div className="animate-spin rounded-full h-12 w-12 border-t-4 border-b-4 border-blue-500"></div></div>;

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-white">
      {/* Header */}
      <header className="bg-white shadow-md p-4 flex justify-between items-center sticky top-0 z-10">
        <div className="text-2xl font-bold text-blue-600">ExperienceHub</div>
        <div className="relative">
          <button onClick={() => setDropdownOpen(!dropdownOpen)} className="flex items-center space-x-2">
            <span className="text-gray-700">{user?.name}</span>
            <svg className="w-5 h-5 text-gray-500" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M5.293 7.293a1 1 0 011.414 0L10 10.586l3.293-3.293a1 1 0 111.414 1.414l-4 4a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414z" clipRule="evenodd" />
            </svg>
          </button>
          {dropdownOpen && (
            <div className="absolute right-0 mt-2 w-48 bg-white border rounded-lg shadow-lg">
              <button onClick={logout} className="block w-full text-left px-4 py-2 text-red-600 hover:bg-gray-100">Logout</button>
            </div>
          )}
        </div>
      </header>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto p-6 grid grid-cols-1 lg:grid-cols-4 gap-6">
        {/* Summary Cards */}
        <div className="lg:col-span-3 grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6 mb-6">
          <div className="bg-gradient-to-r from-blue-400 to-blue-600 p-6 rounded-lg shadow-lg text-white">
            <h3 className="text-sm font-semibold">Total Experiences</h3>
            <p className="text-3xl font-bold">{experiences.length}</p>
          </div>
          <div className="bg-gradient-to-r from-green-400 to-green-600 p-6 rounded-lg shadow-lg text-white">
            <h3 className="text-sm font-semibold">Pending Review</h3>
            <p className="text-3xl font-bold">{pendingExp.length}</p>
          </div>
          <div className="bg-gradient-to-r from-purple-400 to-purple-600 p-6 rounded-lg shadow-lg text-white">
            <h3 className="text-sm font-semibold">Achievements</h3>
            <p className="text-3xl font-bold">{experiences.filter(exp => exp.achievementLevel === 'Winner').length}</p>
          </div>
          <div className="bg-gradient-to-r from-yellow-400 to-yellow-600 p-6 rounded-lg shadow-lg text-white">
            <h3 className="text-sm font-semibold">Profile Views</h3>
            <p className="text-3xl font-bold">0</p> {/* Placeholder, requires backend tracking */}
          </div>
        </div>

        {/* Pending Experiences */}
        <div className="lg:col-span-3 space-y-6">
          <h2 className="text-2xl font-semibold text-gray-800">Pending Experiences</h2>
          {pendingExp.length === 0 ? (
            <p className="text-gray-500">No pending experiences.</p>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {pendingExp.map(exp => (
                <div key={exp._id} className="bg-white p-4 rounded-lg shadow-md hover:shadow-lg transition">
                  <h3 className="text-lg font-semibold text-gray-900">{exp.activityName}</h3>
                  <p className="text-gray-600">Submitted by: {exp.orgId.name || 'Unknown Org'}</p>
                  <p className="text-gray-500 text-sm">{exp.description.substring(0, 100)}...</p>
                  <p className="text-gray-400 text-xs">{new Date(exp.date).toLocaleDateString()} | {exp.category}</p>
                  <div className="mt-4 flex gap-2">
                    <button onClick={() => handleAction(exp._id, 'accept')} className="bg-blue-500 text-white px-3 py-1 rounded hover:bg-blue-600">Accept</button>
                    <button onClick={() => handleAction(exp._id, 'reject')} className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600">Decline</button>
                    <button className="border border-gray-300 text-gray-700 px-3 py-1 rounded hover:bg-gray-100">View Details</button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* My Experiences */}
        <div className="lg:col-span-3 space-y-6">
          <h2 className="text-2xl font-semibold text-gray-800">My Experiences</h2>
          {experiences.length === 0 ? (
            <p className="text-gray-500">No verified experiences yet.</p>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {experiences.map(exp => (
                <div key={exp._id} className="bg-white p-4 rounded-lg shadow-md hover:shadow-lg transition">
                  <h3 className="text-lg font-semibold text-gray-900">{exp.activityName}</h3>
                  <p className="text-gray-600">Organization: {exp.orgId.name || 'Unknown Org'}</p>
                  <p className="text-gray-500 text-sm">{exp.description.substring(0, 100)}...</p>
                  <p className="text-gray-400 text-xs">{new Date(exp.date).toLocaleDateString()}</p>
                  <div className="flex gap-2 mt-2">
                    <span className="px-2 py-1 bg-green-100 text-green-800 text-xs rounded-full">Verified</span>
                    {exp.featured && <span className="px-2 py-1 bg-yellow-100 text-yellow-800 text-xs rounded-full">Featured</span>}
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Profile Sidebar */}
        <aside className="lg:col-span-1 space-y-6">
          <div className="bg-white p-6 rounded-lg shadow-md text-center">
            {/* Profile Picture Upload */}
            <div className="relative w-28 h-28 mx-auto mb-4">
              <img
                src={profile.profilePic || "/default-avatar.png"}
                alt="Profile"
                className="w-28 h-28 rounded-full object-cover border"
              />
              <label className="absolute bottom-0 right-0 bg-blue-500 p-1 rounded-full cursor-pointer">
                <input
                  type="file"
                  accept="image/*"
                  className="hidden"
                  onChange={async (e) => {
                    const file = e.target.files[0];
                    if (file) {
                      const formData = new FormData();
                      formData.append("profilePic", file);

                      try {
                        const config = { headers: { Authorization: `Bearer ${token}`, "Content-Type": "multipart/form-data" } };
                        const res = await axios.post("/api/user/uploadProfilePic", formData, config);
                        setProfile({ ...profile, profilePic: res.data.url }); // assuming backend returns { url }
                      } catch (err) {
                        alert("Error uploading image: " + (err.response?.data?.error || "Unknown error"));
                      }
                    }
                  }}
                />
                <svg className="w-5 h-5 text-white" fill="currentColor" viewBox="0 0 20 20">
                  <path d="M4 3a2 2 0 012-2h2l1-1h2l1 1h2a2 2 0 012 2v2H4V3z" />
                  <path fillRule="evenodd" d="M3 6h14a1 1 0 011 1v9a2 2 0 01-2 2H4a2 2 0 01-2-2V7a1 1 0 011-1z" clipRule="evenodd" />
                </svg>
              </label>
            </div>

            {/* User Info */}
            <h3 className="text-xl font-semibold text-gray-900">{user?.name}</h3>
            <p className="text-gray-600">{user?.email}</p>

            {/* Bio */}
            <textarea
              value={profile.bio}
              onChange={(e) => setProfile({ ...profile, bio: e.target.value })}
              placeholder="Add a bio..."
              className="w-full border-gray-300 p-2 rounded-lg mt-3 focus:ring-2 focus:ring-blue-500"
            />

            {/* Skills */}
            <div className="mt-4 text-left">
              <h4 className="text-sm font-semibold text-gray-700">Skills</h4>
              <div className="flex flex-wrap gap-2 mt-2">
                {profile.skills.map((skill, idx) => (
                  <span key={idx} className="bg-blue-100 text-blue-800 px-2 py-1 rounded-full text-sm flex items-center">
                    {skill} <button onClick={() => removeSkill(skill)} className="ml-1 text-xs">×</button>
                  </span>
                ))}
                <input
                  type="text"
                  onKeyPress={addSkill}
                  placeholder="Add skill..."
                  className="border-gray-300 p-1 rounded focus:ring-2 focus:ring-blue-500"
                />
              </div>
            </div>

            {/* Links */}
            <div className="mt-4 text-left">
              <h4 className="text-sm font-semibold text-gray-700">Links</h4>
              <input
                value={profile.github}
                onChange={(e) => setProfile({ ...profile, github: e.target.value })}
                placeholder="GitHub URL"
                className="w-full border-gray-300 p-2 rounded-lg mt-1 focus:ring-2 focus:ring-blue-500"
              />
              <input
                value={profile.linkedin}
                onChange={(e) => setProfile({ ...profile, linkedin: e.target.value })}
                placeholder="LinkedIn URL"
                className="w-full border-gray-300 p-2 rounded-lg mt-1 focus:ring-2 focus:ring-blue-500"
              />
            </div>

            <button onClick={handleProfileUpdate} className="w-full bg-blue-500 text-white px-4 py-2 rounded-lg mt-4 hover:bg-blue-600 transition">Update Profile</button>
          </div>

          {/* Portfolio Settings */}
          <div className="bg-white p-6 rounded-lg shadow-md">
            <h4 className="text-lg font-semibold text-gray-800">Portfolio Settings</h4>
            <input
              value={profile.customUrl}
              onChange={(e) => setProfile({ ...profile, customUrl: e.target.value })}
              placeholder="Custom Portfolio URL"
              className="w-full border-gray-300 p-2 rounded-lg mt-2 focus:ring-2 focus:ring-blue-500"
            />
            <div className="flex items-center mt-2">
              <input
                type="checkbox"
                checked={profile.isPublic}
                onChange={(e) => setProfile({ ...profile, isPublic: e.target.checked })}
                className="mr-2"
              />
              <span className="text-gray-700">Make Portfolio Public</span>
            </div>
            <Link to={`/portfolio/${user?.username || user?.email}`} className="w-full bg-green-500 text-white px-4 py-2 rounded-lg mt-4 block text-center hover:bg-green-600 transition">View Public Portfolio</Link>
          </div>
        </aside>
      </div>
    </div>
  );
};

export default StudentDashboard;
