import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const StudentDashboard = ({ token, logout }) => {
  const [user, setUser] = useState(null);
  const [experiences, setExperiences] = useState([]);
  const [pendingExp, setPendingExp] = useState([]);
  const [profile, setProfile] = useState({
    bio: '',
    skills: [],
    github: '',
    leetcode: '',
    figma: '',
    linkedin: '',
    customUrl: '',
    isPublic: false,
    profilePic: '',
  });
  const [profilePic, setProfilePic] = useState(null);
  const [preview, setPreview] = useState(null);
  const [loading, setLoading] = useState(true);
  const [uploading, setUploading] = useState(false);
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [expandedExp, setExpandedExp] = useState(null);
  const [inputValue, setInputValue] = useState('');
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      const config = { headers: { Authorization: `Bearer ${token}` } };
      try {
        const [userRes, expRes] = await Promise.all([
          axios.get('/api/user/me', config),
          axios.get('/api/experience/student', config),
        ]);
        setUser(userRes.data);
        const allExp = expRes.data;

        console.log('All experiences fetched:', allExp); // Debug log

        // Updated filtering logic:
        // My Experiences: Only experiences that student has accepted
        const acceptedExperiences = allExp.filter((exp) => exp.status === 'STUDENT_ACCEPTED');
        setExperiences(acceptedExperiences);

        // Pending Experiences: Experiences approved by org but not yet accepted/declined by student
        const pendingExperiences = allExp.filter((exp) => exp.status === 'ORG_APPROVED');
        setPendingExp(pendingExperiences);

        console.log('Accepted experiences:', acceptedExperiences); // Debug log
        console.log('Pending experiences:', pendingExperiences); // Debug log

        const fetchedProfile = {
          bio: userRes.data.profile?.bio || '',
          skills: Array.isArray(userRes.data.profile?.skills) ? userRes.data.profile.skills : [],
          github: userRes.data.profile?.links?.github || '',
          leetcode: userRes.data.profile?.links?.leetcode || '',
          figma: userRes.data.profile?.links?.figma || '',
          linkedin: userRes.data.profile?.links?.linkedin || '',
          customUrl: userRes.data.profile?.publicUrl || '',
          isPublic: userRes.data.profile?.isPublic || false,
          profilePic: userRes.data.profile?.profilePic || '',
        };
        console.log('Fetched profile:', fetchedProfile);
        setProfile(fetchedProfile);
        if (userRes.data.profile?.profilePic) {
          setPreview(`http://localhost:5001${userRes.data.profile?.profilePic}`);
        }
      } catch (err) {
        console.error('Fetch data error:', err.response || err);
        alert('Error fetching data: ' + (err.response?.data?.error || err.message || 'Unknown error'));
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [token]);

  const handleAction = async (id, action) => {
    const config = { headers: { Authorization: `Bearer ${token}` } };
    try {
      console.log(`Performing action: ${action} on experience ${id}`); // Debug log

      await axios.post(`/api/experience/${id}/${action}`, {}, config);

      // Fixed: Refresh the experience data after action with proper filtering
      const res = await axios.get('/api/experience/student', config);
      const allExp = res.data;

      console.log('All experiences after action:', allExp); // Debug log

      // Update both lists based on new status
      const acceptedExperiences = allExp.filter((exp) => exp.status === 'STUDENT_ACCEPTED');
      const pendingExperiences = allExp.filter((exp) => exp.status === 'ORG_APPROVED');

      console.log('Updated accepted experiences:', acceptedExperiences); // Debug log
      console.log('Updated pending experiences:', pendingExperiences); // Debug log

      setExperiences(acceptedExperiences);
      setPendingExp(pendingExperiences);

      // Close expanded view if it was the experience that was acted upon
      if (expandedExp === id) {
        setExpandedExp(null);
      }

    } catch (err) {
      console.error('Action error:', err.response || err);
      alert('Error: ' + (err.response?.data?.error || err.message || 'Unknown error'));
    }
  };

  const isValidUrl = (url) => {
    if (!url) return true;
    try {
      new URL(url);
      return true;
    } catch {
      return false;
    }
  };

  const isValidPublicUrl = (url) => {
    if (!url) return true;
    return /^[a-zA-Z0-9-_]+$/.test(url);
  };

  const handleSaveProfile = async () => {
    try {
      setUploading(true);

      const linksObject = {
        github: profile.github || '',
        leetcode: profile.leetcode || '',
        figma: profile.figma || '',
        linkedin: profile.linkedin || '',
      };

      for (const [key, value] of Object.entries(linksObject)) {
        if (value && !isValidUrl(value)) {
          alert(`Invalid ${key} URL. Please enter a valid URL or leave it empty.`);
          setUploading(false);
          return;
        }
      }

      if (profile.customUrl && !isValidPublicUrl(profile.customUrl)) {
        alert('Invalid custom URL. Use only alphanumeric characters, hyphens, or underscores.');
        setUploading(false);
        return;
      }

      const formData = new FormData();
      formData.append('bio', profile.bio || '');
      formData.append('skills', JSON.stringify(profile.skills || []));
      formData.append('links', JSON.stringify(linksObject));
      formData.append('publicUrl', profile.customUrl || '');
      formData.append('isPublic', String(profile.isPublic));

      if (profilePic && profilePic instanceof File) {
        formData.append('profilePic', profilePic);
        console.log('Uploading file:', profilePic.name, profilePic.type, profilePic.size);
      }

      const formDataLog = {};
      for (let [key, value] of formData.entries()) {
        if (value instanceof File) {
          formDataLog[key] = `File: ${value.name} (${value.size} bytes)`;
        } else {
          formDataLog[key] = value;
        }
      }
      console.log('Sending FormData:', formDataLog);

      const config = {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      };

      const res = await axios.patch('/api/user/profile', formData, config);
      console.log('Profile update response:', res.data);

      if (res.data.profilePic) {
        setPreview(`http://localhost:5001${res.data.profilePic}`);
      }

      alert('Profile updated successfully');

      const userRes = await axios.get('/api/user/me', { headers: { Authorization: `Bearer ${token}` } });
      const updatedProfile = {
        bio: userRes.data.profile?.bio || '',
        skills: Array.isArray(userRes.data.profile?.skills) ? userRes.data.profile.skills : [],
        github: userRes.data.profile?.links?.github || '',
        leetcode: userRes.data.profile?.links?.leetcode || '',
        figma: userRes.data.profile?.links?.figma || '',
        linkedin: userRes.data.profile?.links?.linkedin || '',
        customUrl: userRes.data.profile?.publicUrl || '',
        isPublic: userRes.data.profile?.isPublic || false,
        profilePic: userRes.data.profile?.profilePic || '',
      };
      console.log('Updated profile:', updatedProfile);
      setUser(userRes.data);
      setProfile(updatedProfile);
      setProfilePic(null);
      setIsEditing(false);
    } catch (err) {
      console.error('Profile update error:', err.response?.data || err);
      alert('Error: ' + (err.response?.data?.error || err.message || 'Unknown error'));
    } finally {
      setUploading(false);
    }
  };

  const handleSkillKeyDown = (e) => {
    if (e.key === 'Enter' && inputValue.trim()) {
      e.preventDefault();
      if (!profile.skills.includes(inputValue.trim())) {
        setProfile((prev) => ({
          ...prev,
          skills: [...prev.skills, inputValue.trim()],
        }));
      }
      setInputValue('');
    }
  };

  const removeSkill = (skillToRemove) => {
    setProfile((prev) => ({
      ...prev,
      skills: prev.skills.filter((skill) => skill !== skillToRemove),
    }));
  };

  const handlePicChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      if (file.size > 5 * 1024 * 1024) {
        alert('File size exceeds 5MB limit.');
        return;
      }
      if (!['image/jpeg', 'image/png', 'image/gif'].includes(file.type)) {
        alert('Invalid file type. Only JPEG, PNG, or GIF allowed.');
        return;
      }
      console.log('Selected file:', file.name, file.type, file.size);
      setProfilePic(file);
      setPreview(URL.createObjectURL(file));
    }
  };

  const publishPortfolio = async () => {
    const config = { headers: { Authorization: `Bearer ${token}` } };
    try {
      const res = await axios.post('/api/portfolio/publish', { username: profile.customUrl }, config);
      setProfile((prev) => ({
        ...prev,
        customUrl: res.data.publicUrl.replace('/portfolio/', ''),
        isPublic: true,
      }));
      alert('Portfolio published!');
    } catch (err) {
      console.error('Publish portfolio error:', err.response || err);
      alert('Error: ' + (err.response?.data?.error || err.message || 'Unknown error'));
    }
  };

  const toggleFeatured = async (id) => {
    const config = { headers: { Authorization: `Bearer ${token}` } };
    try {
      await axios.patch(`/api/experience/${id}/feature`, {}, config);
      // Fixed: Properly update state after toggling featured status
      const res = await axios.get('/api/experience/student', config);
      const allExp = res.data;
      setExperiences(allExp.filter((exp) => exp.status === 'STUDENT_ACCEPTED'));
    } catch (err) {
      console.error('Toggle featured error:', err.response || err);
      alert('Error: ' + (err.response?.data?.error || err.message || 'Unknown error'));
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-4 border-b-4 border-blue-500"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-white">
      <header className="bg-white shadow-md p-4 flex justify-between items-center sticky top-0 z-10">
        <div className="text-2xl font-bold text-blue-600">ExperienceHub</div>
        <div className="relative">
          <button
            onClick={() => setDropdownOpen(!dropdownOpen)}
            className="flex items-center space-x-2"
          >
            <span className="text-gray-700">{user?.name}</span>
            <svg className="w-5 h-5 text-gray-500" fill="currentColor" viewBox="0 0 20 20">
              <path
                fillRule="evenodd"
                d="M5.293 7.293a1 1 0 011.414 0L10 10.586l3.293-3.293a1 1 0 111.414 1.414l-4 4a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414z"
                clipRule="evenodd"
              />
            </svg>
          </button>
          {dropdownOpen && (
            <div className="absolute right-0 mt-2 w-48 bg-white border rounded-lg shadow-lg">
              <button
                onClick={logout}
                className="block w-full text-left px-4 py-2 text-red-600 hover:bg-gray-100"
              >
                Logout
              </button>
            </div>
          )}
        </div>
      </header>

      <div className="max-w-7xl mx-auto p-6 grid grid-cols-1 lg:grid-cols-4 gap-6">
        <div className="lg:col-span-3 grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6 mb-6">
          <div className="bg-gradient-to-r from-blue-400 to-blue-600 p-6 rounded-lg shadow-lg text-white">
            <h3 className="text-sm font-semibold">Total Experiences</h3>
            <p className="text-3xl font-bold">{experiences.length}</p>
          </div>
          <div className="bg-gradient-to-r from-green-400 to-green-600 p-6 rounded-lg shadow-lg text-white">
            <h3 className="text-sm font-semibold">Pending Review</h3>
            <p className="text-3xl font-bold">{pendingExp.length}</p>
          </div>
          <div className="bg-gradient-to-r from-purple-400 to-purple-600 p-6 rounded-lg shadow-lg text-white">
            <h3 className="text-sm font-semibold">Achievements</h3>
            <p className="text-3xl font-bold">
              {experiences.filter((exp) => exp.achievementLevel === 'Winner').length}
            </p>
          </div>
          <div className="bg-gradient-to-r from-yellow-400 to-yellow-600 p-6 rounded-lg shadow-lg text-white">
            <h3 className="text-sm font-semibold">Profile Views</h3>
            <p className="text-3xl font-bold">0</p>
          </div>
        </div>

        <div className="lg:col-span-3 space-y-6">
          <h2 className="text-2xl font-semibold text-gray-800">Pending Experiences</h2>
          <p className="text-gray-600 text-sm">These experiences have been verified by organizations and are waiting for your approval.</p>
          {pendingExp.length === 0 ? (
            <p className="text-gray-500">No pending experiences.</p>
          ) : (
            <div className="space-y-4">
              {pendingExp.map((exp) => (
                <div
                  key={exp._id}
                  className="bg-white p-4 rounded-lg shadow-md hover:shadow-lg transition border-l-4 border-orange-500"
                >
                  <div className="flex justify-between items-start">
                    <div className="flex-1">
                      <h3 className="text-lg font-semibold text-gray-900">{exp.activityName}</h3>
                      <p className="text-gray-600">Verified by: {exp.orgId?.name || 'Unknown Org'}</p>
                      <p className="text-gray-500 text-sm mb-2">
                        {exp.description.length > 100 ? `${exp.description.substring(0, 100)}...` : exp.description}
                      </p>
                      <div className="flex gap-2 text-xs">
                        <span className="px-2 py-1 rounded-full bg-blue-100 text-blue-800">{exp.category}</span>
                        <span className="px-2 py-1 rounded-full bg-purple-100 text-purple-800">{exp.achievementLevel}</span>
                        <span className="px-2 py-1 rounded-full bg-gray-100 text-gray-800">{new Date(exp.date).toLocaleDateString()}</span>
                      </div>
                    </div>
                    <span className="px-3 py-1 text-sm rounded-full bg-orange-100 text-orange-800">
                      Awaiting Your Response
                    </span>
                  </div>

                  <div className="mt-4 flex gap-2">
                    <button
                      onClick={() => handleAction(exp._id, 'accept')}
                      className="bg-green-500 text-white px-4 py-2 rounded-lg hover:bg-green-600 transition duration-300 flex items-center gap-2"
                    >
                      ✓ Accept Experience
                    </button>
                    <button
                      onClick={() => handleAction(exp._id, 'decline')}
                      className="bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600 transition duration-300 flex items-center gap-2"
                    >
                      ✗ Decline Experience
                    </button>
                    <button
                      onClick={() => setExpandedExp(expandedExp === exp._id ? null : exp._id)}
                      className="border border-gray-300 text-gray-700 px-4 py-2 rounded-lg hover:bg-gray-100 transition duration-300"
                    >
                      {expandedExp === exp._id ? 'Hide Details' : 'View Details'}
                    </button>
                  </div>

                  {expandedExp === exp._id && (
                    <div className="mt-4 bg-gray-50 p-4 rounded-lg">
                      <p className="mb-2"><strong>Full Description:</strong> {exp.description}</p>
                      <p className="mb-2"><strong>Achievement Level:</strong> {exp.achievementLevel}</p>
                      <p className="mb-2"><strong>Date:</strong> {new Date(exp.date).toLocaleDateString()}</p>
                      <p className="mb-2"><strong>Category:</strong> {exp.category}</p>
                      {exp.documents && exp.documents.length > 0 && (
                        <div>
                          <p className="mb-2"><strong>Documents:</strong></p>
                          <div className="space-y-2">
                            {exp.documents.map((doc, idx) => {
                              const fileName = doc.split('/').pop();
                              return (
                                <a
                                  key={idx}
                                  href={`http://localhost:5001/Uploads/${fileName}`}
                                  target="_blank"
                                  rel="noopener noreferrer"
                                  className="block text-blue-600 hover:text-blue-800 underline"
                                >
                                  Document {idx + 1}: {fileName}
                                </a>
                              );
                            })}
                          </div>
                        </div>
                      )}
                    </div>
                  )}
                </div>
              ))}
            </div>
          )}
        </div>

        <div className="lg:col-span-3 space-y-6">
          <h2 className="text-2xl font-semibold text-gray-800">My Experiences</h2>
          <p className="text-gray-600 text-sm">These are experiences you have accepted and can feature on your portfolio.</p>
          {experiences.length === 0 ? (
            <p className="text-gray-500">No accepted experiences yet. Accept some pending experiences to see them here.</p>
          ) : (
            <div className="space-y-4">
              {experiences.map((exp) => (
                <div
                  key={exp._id}
                  className="bg-white p-4 rounded-lg shadow-md hover:shadow-lg transition border-l-4 border-green-500"
                >
                  <div className="flex justify-between items-start">
                    <div className="flex-1">
                      <h3 className="text-lg font-semibold text-gray-900">{exp.activityName}</h3>
                      <p className="text-gray-600">Organization: {exp.orgId?.name || 'Unknown Org'}</p>
                      <p className="text-gray-500 text-sm mb-2">
                        {exp.description.length > 100 ? `${exp.description.substring(0, 100)}...` : exp.description}
                      </p>
                      <div className="flex gap-2 text-xs">
                        <span className="px-2 py-1 rounded-full bg-blue-100 text-blue-800">{exp.category}</span>
                        <span className="px-2 py-1 rounded-full bg-purple-100 text-purple-800">{exp.achievementLevel}</span>
                        <span className="px-2 py-1 rounded-full bg-gray-100 text-gray-800">{new Date(exp.date).toLocaleDateString()}</span>
                      </div>
                    </div>
                    <button
                      onClick={() => toggleFeatured(exp._id)}
                      className={`text-2xl transition duration-300 ${exp.featured ? 'text-yellow-500' : 'text-gray-300 hover:text-yellow-400'}`}
                      title={exp.featured ? 'Remove from featured' : 'Add to featured'}
                    >
                      {exp.featured ? '★' : '☆'}
                    </button>
                  </div>

                  <div className="flex gap-2 mt-3">
                    <span className="px-3 py-1 bg-green-100 text-green-800 text-xs rounded-full">
                      Accepted
                    </span>
                    {exp.featured && (
                      <span className="px-3 py-1 bg-yellow-100 text-yellow-800 text-xs rounded-full">
                        Featured
                      </span>
                    )}
                  </div>

                  {exp.documents && exp.documents.length > 0 && (
                    <div className="mt-3">
                      <p className="text-sm text-gray-600 mb-2">Documents:</p>
                      <div className="flex flex-wrap gap-2">
                        {exp.documents.map((doc, idx) => {
                          const fileName = doc.split('/').pop();
                          return (
                            <a
                              key={idx}
                              href={`http://localhost:5001/Uploads/${fileName}`}
                              target="_blank"
                              rel="noopener noreferrer"
                              className="text-blue-600 hover:text-blue-800 underline text-sm"
                            >
                              Doc {idx + 1}
                            </a>
                          );
                        })}
                      </div>
                    </div>
                  )}
                </div>
              ))}
            </div>
          )}
        </div>

        <aside className="lg:col-span-1 space-y-6">
          <div className="bg-white p-6 rounded-lg shadow-md text-center">
            <div className="relative w-28 h-28 mx-auto mb-4">
              <label htmlFor="profilePic" className="cursor-pointer">
                <img
                  src={preview || '/assets/user.png'}
                  alt="Profile"
                  className="w-28 h-28 rounded-full object-cover border"
                  onError={(e) => {
                    console.error('Image load error:', profile.profilePic);
                    e.target.src = '/assets/user.png';
                  }}
                />
                {uploading && (
                  <div className="absolute inset-0 flex items-center justify-center bg-black bg-opacity-50 rounded-full">
                    <div className="animate-spin rounded-full h-6 w-6 border-t-2 border-b-2 border-white"></div>
                  </div>
                )}
              </label>
              <input
                type="file"
                id="profilePic"
                accept="image/*"
                className="hidden"
                disabled={uploading || !isEditing}
                onChange={handlePicChange}
              />
            </div>

            <h3 className="text-xl font-semibold text-gray-900">{user?.name}</h3>
            <p className="text-gray-600">{user?.email}</p>

            <textarea
              value={profile.bio}
              onChange={(e) => setProfile({ ...profile, bio: e.target.value })}
              placeholder="Add a bio..."
              className="w-full border-gray-300 p-2 rounded-lg mt-3 focus:ring-2 focus:ring-blue-500"
              disabled={!isEditing}
            />

            <div className="mt-4 text-left">
              <h4 className="text-sm font-semibold text-gray-700">Skills</h4>
              <div className="flex flex-wrap gap-2 mt-2">
                {profile.skills.map((skill, idx) => (
                  <span
                    key={idx}
                    className="bg-blue-100 text-blue-800 px-2 py-1 rounded-full text-sm flex items-center"
                  >
                    {skill}
                    {isEditing && (
                      <button onClick={() => removeSkill(skill)} className="ml-1 text-xs">
                        ×
                      </button>
                    )}
                  </span>
                ))}
                {isEditing && (
                  <input
                    type="text"
                    value={inputValue}
                    onChange={(e) => setInputValue(e.target.value)}
                    onKeyDown={handleSkillKeyDown}
                    placeholder="Add skill..."
                    className="border-gray-300 p-1 rounded focus:ring-2 focus:ring-blue-500"
                  />
                )}
              </div>
            </div>

            <div className="mt-4 text-left">
              <h4 className="text-sm font-semibold text-gray-700">Links</h4>
              <input
                value={profile.github}
                onChange={(e) => setProfile({ ...profile, github: e.target.value })}
                placeholder="GitHub URL"
                className="w-full border-gray-300 p-2 rounded-lg mt-1 focus:ring-2 focus:ring-blue-500"
                disabled={!isEditing}
              />
              <input
                value={profile.leetcode}
                onChange={(e) => setProfile({ ...profile, leetcode: e.target.value })}
                placeholder="LeetCode URL"
                className="w-full border-gray-300 p-2 rounded-lg mt-1 focus:ring-2 focus:ring-blue-500"
                disabled={!isEditing}
              />
              <input
                value={profile.figma}
                onChange={(e) => setProfile({ ...profile, figma: e.target.value })}
                placeholder="Figma URL"
                className="w-full border-gray-300 p-2 rounded-lg mt-1 focus:ring-2 focus:ring-blue-500"
                disabled={!isEditing}
              />
              <input
                value={profile.linkedin}
                onChange={(e) => setProfile({ ...profile, linkedin: e.target.value })}
                placeholder="LinkedIn URL"
                className="w-full border-gray-300 p-2 rounded-lg mt-1 focus:ring-2 focus:ring-blue-500"
                disabled={!isEditing}
              />
            </div>

            {isEditing ? (
              <button
                onClick={handleSaveProfile}
                className="w-full bg-blue-500 text-white px-4 py-2 rounded-lg mt-4 hover:bg-blue-600 transition"
                disabled={uploading}
              >
                Save Profile
              </button>
            ) : (
              <button
                onClick={() => setIsEditing(true)}
                className="w-full bg-gray-500 text-white px-4 py-2 rounded-lg mt-4 hover:bg-gray-600 transition"
              >
                Edit Profile
              </button>
            )}
          </div>

          <div className="bg-white p-6 rounded-lg shadow-md">
            <h4 className="text-lg font-semibold text-gray-800">Portfolio Settings</h4>
            <input
              value={profile.customUrl}
              onChange={(e) => setProfile({ ...profile, customUrl: e.target.value })}
              placeholder="Custom Portfolio URL"
              className="w-full border-gray-300 p-2 rounded-lg mt-2 focus:ring-2 focus:ring-blue-500"
              disabled={profile.isPublic || !isEditing}
            />
            <div className="flex items-center mt-2">
              <input
                type="checkbox"
                checked={profile.isPublic}
                onChange={(e) => setProfile({ ...profile, isPublic: e.target.checked })}
                className="mr-2"
                disabled={!isEditing}
              />
              <span className="text-gray-700">Make Portfolio Public</span>
            </div>
            <button
              onClick={publishPortfolio}
              className="w-full bg-green-500 text-white px-4 py-2 rounded-lg mt-4 hover:bg-green-600 transition"
              disabled={profile.isPublic || !isEditing}
            >
              Publish Portfolio
            </button>
            {profile.isPublic && (
              <Link
                to={`/portfolio/${user?.username || user?.email.split('@')[0]}`}
                className="w-full block text-center bg-blue-500 text-white px-4 py-2 rounded-lg mt-2 hover:bg-blue-600 transition"
              >
                View Public Portfolio
              </Link>
            )}
          </div>
        </aside>
      </div>
    </div>
  );
};

export default StudentDashboard;
