import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaSignOutAlt } from 'react-icons/fa';
import ExperienceCard from './ExperienceCard';
import PortfolioBuilder from './PortfolioBuilder';

const StudentDashboard = ({ token, logout }) => {
  const [experiences, setExperiences] = useState([]);
  const [user, setUser] = useState({
    profile: { bio: '', description: '', profilePic: '', skills: [], links: { github: '', leetcode: '', figma: '', linkedin: '' } },
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      const config = { headers: { Authorization: `Bearer ${token}` } };
      try {
        const userRes = await axios.get('/api/user', config);
        setUser(userRes.data);
        const expRes = await axios.get('/api/experience', config);
        setExperiences(expRes.data);
      } catch (err) {
        alert('Error fetching data: ' + (err.response?.data?.error || 'Unknown error'));
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [token]);

  const updateExperiences = async () => {
    const config = { headers: { Authorization: `Bearer ${token}` } };
    try {
      const expRes = await axios.get('/api/experience', config);
      setExperiences(expRes.data);
    } catch (err) {
      alert('Error: ' + (err.response?.data?.error || 'Unknown error'));
    }
  };

  // handleAccept, handleDecline, toggleFeatured unchanged
  // ...

  if (loading) return <div className="flex justify-center items-center h-screen"> <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500"></div> </div>;

  return (
    <div className="p-6 max-w-7xl mx-auto">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold tracking-tight text-gray-900">Student Dashboard</h1>
        <button onClick={logout} className="flex items-center text-red-600 hover:text-red-800">
          <FaSignOutAlt className="mr-2" /> Logout
        </button>
      </div>
      <PortfolioBuilder user={user} token={token} setUser={setUser} />
      <h2 className="text-2xl font-semibold mb-4 text-gray-800">Your Experiences</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {experiences.length === 0 ? (
          <p className="col-span-full text-center text-gray-500">No experiences yet.</p>
        ) : (
          experiences.map(exp => (
            <ExperienceCard key={exp._id} exp={exp} onAccept={handleAccept} onDecline={handleDecline} onToggleFeatured={toggleFeatured} role="student" />
          ))
        )}
      </div>
    </div>
  );
};

export default StudentDashboard;
