import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import api from '../services/api';

function PublicPortfolio() {
  const { username } = useParams();
  const [portfolio, setPortfolio] = useState(null);

  useEffect(() => {
    const fetchPortfolio = async () => {
      try {
        const response = await api.get(`/api/portfolio/by-username/${username}`);
        setPortfolio(response.data);
      } catch (err) {
        alert('Failed to fetch portfolio: ' + err.message);
      }
    };
    fetchPortfolio();
  }, [username]);

  if (!portfolio) return <div className="p-4">Loading...</div>;

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">{portfolio.name}'s Portfolio</h2>
      <p className="mb-2"><strong>Bio:</strong> {portfolio.bio || 'Not provided'}</p>
      <p className="mb-2"><strong>Skills:</strong> {portfolio.skills?.join(', ') || 'None'}</p>
      <div className="mb-2">
        <strong>Links:</strong>
        <ul className="list-disc pl-5">
          {portfolio.links.github && <li>GitHub: {portfolio.links.github}</li>}
          {portfolio.links.leetcode && <li>LeetCode: {portfolio.links.leetcode}</li>}
          {portfolio.links.figma && <li>Figma: {portfolio.links.figma}</li>}
          {portfolio.links.linkedin && <li>LinkedIn: {portfolio.links.linkedin}</li>}
        </ul>
      </div>
    </div>
  );
}

export default PublicPortfolio;
